	return (
		<div style={({ alignItems: "center" }, { justifyContent: "center" })}>
			<Typography variant="overline" style={{ color: "#32CD32" }}>
				Click DOWNLOAD below to save your QR Code.
			</Typography>
			<QRCode
				id="qrcodecanvas"
				value={props.qrData}
				size={350}
				includeMargin={true}
				level={"H"}
				imageSettings={{
					src: logosmall,
					x: null,
					y: null,
					height: 50,
					width: 50,
					excavate: true
				}}
			/>
			<Button onClick={downloadQR} variant="outlined" color="primary">
				Save QR Code
